import React from 'react'
import {SearchOutlined, Notifications} from '@material-ui/icons'
import {Grid} from '@material-ui/core'
import {Star} from '@material-ui/icons'
import colors from '../../tools/colors'
import {connect} from 'react-redux'

const CardMovie = (props) => {
  const {data, movie} = props
  var selectGenre = [];
  const genre = data.genre_ids;
  movie.genre.map((gnr) => {
    genre.map((gnr2) => {
      if(gnr2 === gnr.id){
        selectGenre.push(gnr.name)
      }
    })
  })
  var genres = ''
  if(selectGenre.length > 0){
      for(var i = 0; i < selectGenre.length; i++){
        if(i !== selectGenre - 1){
          genres += selectGenre[i]+','
        }else{
          genres += selectGenre[i]
        }
        if(i === 1){
          break;
        }
      }
  }
  const imageUrl = process.env.MOVIE_IMAGE_URL+data.poster_path
  const title = JSON.stringify(data.title)
  const fontSizeTitle = () => {
    if(title.length > 50){
      return 10
    }else if(title.length > 20){
      return 12
    }else if(title.length > 10){
      return 14
    }else{
      return 16
    }
  }
  return (
    <Grid container spacing={1}>
        <Grid xs={12} sm={12} md={12} lg={12} xl={12} item>
          <div className="img-movie-div">
            <img src={imageUrl} className="img-movie" />
          </div>
        </Grid>
        <Grid xs={12} sm={12} md={12} lg={12} xl={12} item>
          <div className="detail-movie">
            <p className="genre-date">{genres.length > 0 && genres} | {data.release_date}</p>
            <p className="title-movie" style={{fontSize : fontSizeTitle(), width : 190}}>{data.title}</p>
            <p className="rating-movie"><Star style={{color : colors.yellow2, fontSize : 12}} /> &nbsp;{data.vote_average}/10</p>
          </div>
        </Grid>
        <style jsx>{`
          .container-CardMovie {
                display: flex;
                flex-direction:column;
                font-family: Poppins;
          }
          .img-movie-div {
            height: 200px;
            width: 125px;
            border-radius: 10px;

          }

          .img-movie {
            height: 200px;
            width: 125px;
            border-radius: 10px;
          }

          .detail-movie {
            width : 200px;
          }
          .genre-date {
            font-size: 10px;
            font-weight: 400;
            font-family: Poppins;
            width: 200px;
            margin-bottom : -10px
          }
          .title-movie {
            font-weight: 700;
            font-family: Poppins;
            widht: 190px;
            margin-bottom : -10px
          }
          .rating-movie {
            font-size: 10px;
            font-weight: 700;
            font-family: Poppins;
            line-height : 5px;
          }
          .p-movie {
            hyphens: auto;
            widht:200px
          }



          `}</style>
    </Grid>
  )
}

const mapStateToProps = (state) => ({
  movie : state.movie
})

export default connect(mapStateToProps)(CardMovie)
