import React from 'react'
import {SearchOutlined, Notifications} from '@material-ui/icons'
import {Grid} from '@material-ui/core'
import {Star} from '@material-ui/icons'
import colors from '../../tools/colors'

const CardMovie = (props) => {
  const imagUrl = process.env.MOVIE_IMAGE_URL+props.poster_path
  const rate = parseFloat(parseInt(props.vote_count) / (parseint(props.vote_count)/10)).toFixed(2)
  return (
    <Grid container spacing={1}>
        <Grid xs={12} sm={12} md={12} lg={12} xl={12} item>
          <div className="img-movie-div">
            <img src="" className="img-movie" />
          </div>
        </Grid>
        <Grid xs={12} sm={12} md={12} lg={12} xl={12} item>
          <div className="detail-movie">
            <p className="genre-date">Action, scifi | {props.release_date}</p>
            <p className="title-movie">{props.title}</p>
            <p className="rating-movie"><Star style={{color : colors.yellow2, fontSize : 12}} /> &nbsp;{rate}/10</p>
          </div>
        </Grid>
        <style jsx>{`
          .container-CardMovie {
                display: flex;
                flex-direction:column;
                font-family: Poppins;
          }
          .img-movie-div {
            height: 200px;
            width: 125px;
            border-radius: 10px;

          }

          .img-movie {
            height: 200px;
            width: 125px;
            border-radius: 10px;
          }

          .detail-movie {
            width : 200px;
          }
          .genre-date {
            font-size: 10px;
            font-weight: 400;
            font-family: Poppins;
            line-height : 5px;
          }
          .title-movie {
            font-size: 16px;
            font-weight: 700;
            font-family: Poppins;
            line-height : 5px;
          }
          .rating-movie {
            font-size: 10px;
            font-weight: 700;
            font-family: Poppins;
            line-height : 5px;
          }



          `}</style>
    </Grid>
  )
}
export default CardMovie
