import React from 'react'
import {connect} from 'react-redux'
import ItemMenuGenre from './ItemMenuGenre'
import {Grid} from '@material-ui/core'
import {GetGenre, PostActiveGenre} from '../../redux/actions/movie'
import axios from 'axios'

var data = [1,2,3]
class MenuGenre extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            genres : []
        }
    }
    componentDidMount(){
        this.props.dispatch(GetGenre())
        this.props.dispatch(PostActiveGenre(this.props.movie.genre[0].name))
    }
    render(){
        const {activeGenre, genre} = this.props.movie
        return (
            <>
                <Grid container spacing={1}>
                    {
                        (genre.length > 0) &&
                        genre.map(value => (
                            <Grid item key={value.id}><ItemMenuGenre title={value.name} active={(value.name === activeGenre)?true:false} /></Grid>
                        ))
                    }
                </Grid>
            </>
        )
    }
}

const mapStateToProps = (state) => ({
    movie : state.movie
})

export default connect(mapStateToProps)(MenuGenre)
