import React from 'react'
import {connect} from 'react-redux'
import {Grid, Card, CardHeader, CardContent ,Button} from '@material-ui/core'
import {GetGenre, PostActiveGenre, GetMovieGenre} from '../../redux/actions/movie'
import axios from 'axios'
import colors from '../../tools/colors';
import {Formik, Form, Field, ErrorMessage} from 'formik'
import {AUTH_GET_REQ_TOKEN, AUTH_GET_SESSION_TOKEN, AUTH_VALIDATE_LOGIN} from '../../redux/constan/api'
import {} from '../../redux/actions/auth'

const Login = () => {
        return (
            <>
               <div className="login">
                <div className="card-login">
                    <div className="card-header">
                        <h4 className="head-login">Login ke {process.env.APP_NAME}</h4>
                    </div>
                    <div className="">
                        <Formik
                            initialValues={{ username: '', password: '' }}
                            validate={values => {
                                const errors = {};

                                return errors;
                            }}
                            onSubmit={(values, { setSubmitting }) => {
                                axios.get(process.env.MOVIE_API+AUTH_GET_REQ_TOKEN+'?api_key'+process.env.API_KEY)
                                .then((access) => {
                                    const accessToken = access.data.request_token
                                    axios.post(process.env.MOVIE_API+AUTH_GET_REQ_TOKEN+'?api_key='+process.env.API_KEY, {
                                        username : values.username,
                                        password : values.password,
                                        request_token : accessToken
                                    }, {
                                        headers : {
                                            'Content-Type' : 'application/json'
                                        }
                                    })
                                    .then(() => {
                                        axios.post(process.env.MOVIE_API+AUTH_GET_SESSION_TOKEN+'?api_key='+process.env.API_KEY, {
                                            request_token : accessToken
                                        }, {
                                            headers : {
                                                'Content-Type' : 'application/json'
                                            }
                                        })
                                        .then((session) => {

                                        })
                                    })
                                })
                            }}
                            >
                            {({ 
                                 values,
                                 errors,
                                 touched,
                                 handleChange,
                                 handleBlur,
                                 handleSubmit,
                                 isSubmitting,
                             }) => (
                                <form onSubmit={handleSubmit}>
                                    <div className="form">
                                        <div className="div-field">
                                            <div class="row">
                                                <div class="input-field col s12" style={{width:350}}>
                                                    <input 
                                                        id="username" 
                                                        type="text" 
                                                        class="validate" 
                                                        name="username" 
                                                        onChange={handleChange}
                                                        onBlur={handleBlur}
                                                        value={values.email}
                                                        style={{width:350}}
                                                        />
                                                    <label for="username">Username</label>
                                                    <span class="helper-text" data-error={errors.username && touched.username && errors.username} data-success="right"></span>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="div-field">
                                            <div class="row">
                                                <div class="input-field col s12 field" style={{width:350}}>
                                                    <input
                                                        id="password" 
                                                        type="password" 
                                                        class="validate" 
                                                        name="password" 
                                                        onChange={handleChange}
                                                        onBlur={handleBlur}
                                                        value={values.email}
                                                        style={{width:350}}
                                                    />
                                                    <label for="password">Password</label>
                                                    <span class="helper-text" data-error={errors.password && touched.password && errors.password} data-success="right"></span>
                                                </div>
                                            </div>
                                        </div>
                                        <Button variant="contained" style={{width : 300, background : (isSubmitting)?colors.blue1:colors.blue2}} 
                                            disabled={isSubmitting} type="submit"
                                        >
                                            Login
                                        </Button>
                                    </div>
                                </form>
                            )}
                        </Formik>
                    </div>
                </div>
               </div>
               <style jsx>{`
                    .login {
                        display:flex;
                        justify-content: center;
                    }
                    .card-login {
                        width: 500px;
                        display: flex;
                        flex-direction: column;
                        background: #ffffff;
                        box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
                        transition: 0.3s;
                        position: abosulute;
                        top: 50%;
                        left: 50%;
                        height:500px;
                    }
                    .card-login:hover {
                        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2);
                    }

                    .head-login {
                        font-family: Poppins;
                        font-size: 16px;
                        font-color: ${colors.blackPrimary};
                        text-align: center;
                        font-weight: 400;
                    }
                    .form {
                        display: flex;
                        flex-direction: column;
                        margin-top : 50px;
                        align-items:center;
                    }
                    .div-field {
                        display: flex;
                        flex-direction: column;
                        margin-bottom: 20px;
                        width: 350px;
                    }

               `}</style>
            </>
        )
}
const mapStateToProps = (state) => ({
    auth : state.auth
})

export default connect(mapStateToProps)(Login)
