import React from 'react'
import {HomeOutlined, GridOffOutlined, ShareOutlined, BarChart, AccountBoxOutlined, 
    DynamicFeedOutlined, FileCopyOutlined} from '@material-ui/icons'
import {Grid, Link} from '@material-ui/core'


const dataMenu = [{
        title : 'Home',
        icon : <HomeOutlined className="icon-menu"/>
    },{
        title : 'Browse',
        icon : <GridOffOutlined className="icon-menu" />
    },{
        title : 'Category',
        icon : <ShareOutlined className="icon-menu" />
    },{
        title : 'Trending',
        icon : <BarChart className="icon-menu"/>
    },{
        title : 'Profile',
        icon : <AccountBoxOutlined className="icon-menu"/>
    },{
        title : 'Recently Played',
        icon : <DynamicFeedOutlined className="icon-menu"/>
    },{
        title : 'Recently Played',
        icon : <FileCopyOutlined className="icon-menu"/>
    }
]
const Menu = () => {
  return (
    <div className="container-menu">
      <div className="logo">
          MOVIE
      </div>
      <Grid container justify="center">
        {
            dataMenu.map((value) => (
                <Grid key={value.title} item xs={12} sm={12} lg={12} xl={12}>
                    <Link href="#" color="inherit">
                        <Grid container style={{alignItems:'center', justifyContent : 'center'}}>
                            <Grid item xs={2}>{value.icon}</Grid>
                            <Grid item xs={10}><p className="title-menu">{value.title}</p></Grid>
                        </Grid>
                    </Link>
                </Grid>
            ))
          }
      </Grid>
      <style jsx>{`
        @import url('https://fonts.googleapis.com/css?family=Poppins&display=swap');
        .container-menu {
            display:flex;
            flex-direction:column;
            text-align: center;
            background:#ffffff;
        }
        .logo {
            margin-bottom:25px;
            font-weight:bold;
            font-size:20px;
            font-family: Poppins;
            display: inline-block;
        }
        .menu {
            justify-content:center;
            display: inline-block;
        }
        .title-menu {
            font-color: #9b9b9b;
            font-size:14px;
            font-family: Poppins;
        }
        .icon-menu {
            font-size:14px;
            flex:1;
            margin-right:5px;
            font-color:#9b9b9b;
        }
      `}</style>
    </div>
  )
}

Menu.propTypes = {

}

export default Menu
