import React from 'react'
import {HomeOutlined, GridOffOutlined, ShareOutlined, BarChart, AccountBoxOutlined, 
    DynamicFeedOutlined, FileCopyOutlined} from '@material-ui/icons'
import {Grid, Link} from '@material-ui/core'
import { connect } from 'react-redux'


const dataMenu = [{
        title : 'Home',
        icon : <HomeOutlined className="icon-menu" style={{color:"#5a5a5a", textAlign:'center', fontSize:18, marginRight:15}}/>
    },{
        title : 'Browse',
        icon : <GridOffOutlined className="icon-menu" style={{color:"#5a5a5a", fontSize:18, marginRight:15}}/>
    },{
        title : 'Category',
        icon : <ShareOutlined className="icon-menu" style={{color:"#5a5a5a", fontSize:18, marginRight:15}} />
    },{
        title : 'Trending',
        icon : <BarChart className="icon-menu" style={{color:"#5a5a5a", fontSize:18, marginRight:15}}/>
    },{
        title : 'Profile',
        icon : <AccountBoxOutlined className="icon-menu" style={{color:"#5a5a5a", fontSize:18, marginRight:15}}/>
    }
]
const LocalMenu = [{
    title : 'Recently Played',
    icon : <DynamicFeedOutlined className="icon-menu" style={{color:"#5a5a5a", fontSize:18, marginRight:15}}/>
},{
    title : 'Recently Played',
    icon : <FileCopyOutlined className="icon-menu" style={{color:"#5a5a5a", fontSize:18, marginRight:15}}/>
}]
const Menu = () => {
  return (
    <div className="container-menu">
      <div className="logo">
          MOVIE
      </div>
      <Grid container justify="center" style={{display:'inline-block'}}>
        {
            dataMenu.map((value) => (
                <Grid key={value.title} item xs={12} sm={12} lg={12} xl={12}>
                    <Link href="#" color="inherit">
                        <div className="item-menu">
                            {value.icon}
                            <p className="title-menu">{value.title}</p>
                        </div>
                    </Link>
                </Grid>
            ))
          }
          <div className="item-menu">
            <p className="span-menu">My Movie</p>
          </div>
          {
            LocalMenu.map((value) => (
                <Grid key={value.title} item xs={12} sm={12} lg={12} xl={12}>
                    <Link href="#" color="inherit">
                        <div className="item-menu">
                            {value.icon}
                            <p className="title-menu">{value.title}</p>
                        </div>
                    </Link>
                </Grid>
            ))
          }
      </Grid>
      <style jsx>{`
        @import url('https://fonts.googleapis.com/css?family=Poppins&display=swap');
        .container-menu {
            display:inline-block;
            flex-direction:column;
            text-align: center;
            background:#ffffff;
        }
        .logo {
            margin-bottom:40px;
            font-weight:bold;
            font-size:20px;
            font-family: Poppins;
            display: inline-block;
        }
        .menu {
            justify-content:center;
            text-align: center;
            display: inline-block;
        }
        .item-menu {
            display:flex;
            flex-direction:row;
            align-items:center;
            margin-left:35px;
        }
        .title-menu {
            color: #5a5a5a;
            font-size:14px;
            font-family: Poppins;
            font-weight:bold;
        }
        .span-menu {
            color: #a1a1a1;
            font-size:14px;
            font-family: Poppins;
            font-weight:bold;
            margin-top: 20px;
            margin-bottom: 20px;
        }
      `}</style>
    </div>
  )
}

Menu.propTypes = {

}

const mapStateToProps = (state) => ({
    auth : state.auth,
    nav : state.nav
  })


export default connect(mapStateToProps)(Menu)
