import React from 'react'
import {HomeOutlined, GridOffOutlined, ShareOutlined, BarChart, AccountBoxOutlined, 
    DynamicFeedOutlined, FileCopyOutlined} from '@material-ui/icons'
import {Grid} from '@material-ui/core'


const dataMenu = [{
        title : 'Home',
        icon : <HomeOutlined className="icon-menu"/>
    },{
        title : 'Browse',
        icon : <GridOffOutlined className="icon-menu" />
    },{
        title : 'Category',
        icon : <ShareOutlined className="icon-menu" />
    },{
        title : 'Trending',
        icon : <BarChart className="icon-menu"/>
    },{
        title : 'Profile',
        icon : <AccountBoxOutlined className="icon-menu"/>
    },{
        title : 'Recently Played',
        icon : <DynamicFeedOutlined className="icon-menu"/>
    },{
        title : 'Recently Played',
        icon : <FileCopyOutlined className="icon-menu"/>
    }
]
const Menu = () => {
  return (
    <div className="container-menu">
      <div className="logo">
          MOVIE
      </div>
      <Grid container justify="center">
        {
            dataMenu.map((value) => (
            <Grid key={value.title} item xs={12} sm={12} lg={12} xl={12} container style={{alignItems:'center', justifyContent : 'center'}}>
                <Grid item xs={2}>{value.icon}</Grid>
                <Grid item xs={10}><p className="titleMenu">{value.title}</p></Grid>
            </Grid>
            ))
          }
      </Grid>
      <style jsx>{`
        .container-menu {
            justify-content: center;
            background:#ffffff;
            align-item: center;
        }
        .logo {
            margin-bottom:25px;
            font-weight:bold,
            font-size:20px;
        }
        .menu {
            justify-content:center;
        }
        .itemMenu {
            display:flex;
            flex-direction:row;
            justify-content:start;
            align-items:center;
            margin-bottom : 15px;
        }
        .icon-menu {
            font-size:16px;
            flex:1;
            margin-right:5px;
        }
      `}</style>
    </div>
  )
}

Menu.propTypes = {

}

export default Menu
