import {GET_GENRE, GET_ACTIVE_GENRE} from '../constan/movie'

const initialState = {
    genre : [],
    movie : [],
    moviesGenre : [],
    movies : [],
    activeGenre : ''
};

export default (state = initialState, action) => {
  switch (action.type) {
    case GET_GENRE:
      return {
        ...state,           
        genre : action.payload.genres,
      }

    case GET_ACTIVE_GENRE:
      return {
        ...state,           
        genre : action.payload,
      }


    default:
      return state;
  }
};
