import React from 'react'
import {HomeOutlined, GridOffOutlined, ShareOutlined, BarChart, AccountBoxOutlined, DynamicFeedOutlined, FileCopyOutlined} from '@material-ui/icons'


const dataMenu = [{
        title : 'Home',
        icon : <HomeOutlined className="icon-menu"/>
    },{
        title : 'Browse',
        icon : <GridOffOutlined className="icon-menu" />
    },{
        title : 'Category',
        icon : <ShareOutlined className="icon-menu" />
    },{
        title : 'Trending',
        icon : <BarChart className="icon-menu"/>
    },{
        title : 'Profile',
        icon : <AccountBoxOutlined className="icon-menu"/>
    },{
        title : 'Recently Played',
        icon : <DynamicFeedOutlined className="icon-menu"/>
    },{
        title : 'Recently Played',
        icon : <FileCopyOutlined className="icon-menu"/>
    }
]
const Menu = () => {
  return (
    <div className="container-menu">
      <div className="logo">
          MOVIE
      </div>
      <div className="menu">
          {
              dataMenu.map((item) => (
                <div className="itemMenu">
                    {item.icon}
                    <p className="titleMenu">{item.title}</p>
                </div>
              ))
          }
      </div>
      <style jsx>{`
        .container-menu {
            flex: 1;
            justify-content: center;
            align-items: center;
        }
        .logo {
            margin-bottom:25px;
            font-weight:bold,
            font-size:20px
        }
        .itemMenu {
            display:flex;
            flex-direction:row;
            justify-content:space-between;
            margin-bottom : 15px;
        }
        .icon-menu {
            font-size:16px;
            flex:1;
        }
      `}</style>
    </div>
  )
}

Menu.propTypes = {

}

export default Menu
